/**
 * @file test/parse-test.js
 * @author graeme@converse.ai
 * @description Parse a date from any format.
 *
 * Generated by the converse-cli tool for use with the ConverseAI
 * Plugins SDK. https://developers.converse.ai/
 */

const test = require('./request-parse');

describe('Parsed', function () {

  /* * * * * * * * * * * * * * * * * *
  * Parsed Input
  * * * * * * * * * * * * * * * * * */

  it('with parsed input ({iso: 2017-07-02T21:30:00+00:00})', function(done) {
    test({
      input: {
        years: 2017,
        months: 7,
        date: 2,
        hours: 21,
        minutes: 30,
        seconds: 0,
        milliseconds: 0,
        offset: '+00:00',
        iso: '2017-07-02T21:30:00+00:00',
        utc: '2017-07-02T21:30:00+00:00',
        unix: 1499031000
      },
      offset: undefined,
      custom_offset: undefined,
      timezone_offset: undefined
    }, {
      years: 2017,
      months: 7,
      date: 2,
      hours: 21,
      minutes: 30,
      seconds: 0,
      milliseconds: 0,
      offset: '+00:00',
      iso: '2017-07-02T21:30:00+00:00',
      utc: '2017-07-02T21:30:00+00:00',
      unix: 1499031000
    }, done);
  })

  /* * * * * * * * * * * * * * * * * *
  * Parsed Input & Offset
  * * * * * * * * * * * * * * * * * */

  it('with parsed input ({iso: 2017-07-02T21:30:00+00:00}) & static offset (-07:00)', function(done) {
    test({
      input: {
        years: 2017,
        months: 7,
        date: 2,
        hours: 21,
        minutes: 30,
        seconds: 0,
        milliseconds: 0,
        offset: '+00:00',
        iso: '2017-07-02T21:30:00+00:00',
        utc: '2017-07-02T21:30:00+00:00',
        unix: 1499031000
      },
      offset: '-07:00',
      custom_offset: undefined,
      timezone_offset: undefined
    }, {
      years: 2017,
      months: 7,
      date: 2,
      hours: 21,
      minutes: 30,
      seconds: 0,
      milliseconds: 0,
      offset: '-07:00',
      iso: '2017-07-02T21:30:00-07:00',
      utc: '2017-07-03T04:30:00+00:00',
      unix: 1499056200
    }, done);
  })

  it('with parsed input ({iso: 2017-07-02T14:30:00+07:00}) & static offset (-07:00)', function(done) {
    test({
      input: {
        years: 2017,
        months: 7,
        date: 3,
        hours: 4,
        minutes: 30,
        seconds: 0,
        milliseconds: 0,
        offset: '+07:00',
        iso: '2017-07-02T14:30:00+07:00',
        utc: '2017-07-02T21:30:00+00:00',
        unix: 1499031000
      },
      offset: '-07:00',
      custom_offset: undefined,
      timezone_offset: undefined
    }, {
      years: 2017,
      months: 7,
      date: 2,
      hours: 21,
      minutes: 30,
      seconds: 0,
      milliseconds: 0,
      offset: '-07:00',
      iso: '2017-07-02T21:30:00-07:00',
      utc: '2017-07-03T04:30:00+00:00',
      unix: 1499056200
    }, done);
  })

});
